mapper.select:
select h.aptCode, h.apartmentName, h.buildyear, h.dongCode, h.dong, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where dongCode = #{dong}
and h.apartmentName like concat('%', #{aptName}, '%')
order by h.apartmentName limit #{start}, #{spp}

select count(h.aptCode) from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where dongCode = #{dong}
and h.apartmentName like concat('%', #{aptName}, '%')
select sidoName, gugunName, dongName, dongCode, lat, lng from baseaddress where left(dongCode,2) = #{sido} order by lat, lng
select sidoName, gugunName, dongName, dongCode, lat, lng from baseaddress where left(dongCode, 5) = #{gugun} order by lat, lng
select left(sidoCode,2) sidoCode, sidoName from sidocode order by sidoCode
select left(gugunCode,5) gugunCode, gugunName from guguncode where left(gugunCode,2) = #{sido} order by gugunCode
select distinct dong, dongCode from houseinfo where left(dongCode, 5) = #{gugun} order by dong
select h.aptCode, h.apartmentName, h.buildyear, h.dongCode, h.dong, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where dongCode = #{dong} order by apartmentName
select hd.no, hd.dealAmount, hd.dealYear, hd.dealMonth, hd.dealDay, hd.area, hd.floor, hd.cancelDealType, hd.aptCode from houseinfo h, housedeal hd where hd.aptCode = #{aptCode} limit #{dealCount}
select h.aptCode, h.apartmentName, h.buildyear, h.dongCode, h.dong, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where dongCode = #{dongCode} order by apartmentName
select h.aptCode, h.aptName, h.buildyear, h.dong, h.dongName, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) left join dongcode dong_c on left(h.dongcode,9) = left(dong_c.dongcode,9) where h.aptName = #{aptName} and h.dongcode=#{dong}
