mapper.select:
select hd.no, hd.dealAmount, hd.dealYear, hd.dealMonth, hd.dealDay, hd.area, hd.floor, hd.cancelDealType, hd.aptCode
from houseinfo h, housedeal hd 
where hd.aptCode = #{aptCode}
limit #{dealCount}

select h.aptCode, h.apartmentName, h.buildyear, h.dongCode, h.dong, h.jibun, h.lat, h.lng, si.sidoname, gu.gugunname, (select dealAmount from housedeal where aptCode = h.aptCode and no = (select max(no) from housedeal where aptCode = h.aptCode)) recentPrice from houseinfo h left join sidocode si on left(h.dongcode,2) = left(si.sidocode,2) left join guguncode gu on left(h.dongcode,5) = left(gu.guguncode,5) where dongCode = #{dong}
and h.apartmentName like concat('%', #{aptName}, '%')
order by h.apartmentName limit #{start}, #{spp}
