mapper.insert:
insert into board(
	subject, userid, content
) values (
	#{subject}, #{userid}, #{content}
)

mapper.update:
update board 
   set subject = #{subject}, 
   	   userid = #{userid}, 
   	   content = #{content}
 where articleno = #{articleno}

update board 
   set hit = hit+1
 where articleno = #{articleno}

mapper.delete:
    delete from boardLike
	where articleno=#{articleno}
	and userid=#{userId};

mapper.select:
	    SELECT count(*)
	    from boardLike
	    where articleno = #{articleno}

            and userId=#{userId}
